import {ReactComponent as Estrelacheia} from 'assets/img/Estrelacheia.svg';
import {ReactComponent as Estrelamedia} from 'assets/img/Estrelamedia.svg';
import {ReactComponent as Estrelavazia} from 'assets/img/Estrelavazia.svg';
import { type } from 'node:os';
import './styles.css';

type Props = {
  score: number;
}

type StarProps = {
  fill: number;
}

// EX:
// getFills(3.5) => [1, 1, 1, 0.5, 0]
// getFills(4.1) => [1, 1, 1, 1, 0.5]


function getFills(score: number) {

    const fills = [0, 0, 0, 0, 0];

    const integerPart = Math.floor(score);

    for (let i = 0; i < integerPart; i++) {
      fills[i] = 1;
    }

    const diff = score - integerPart;
    if (diff > 0) {
      fills[integerPart] = 0.5;
    }

    return fills;
  }

  function Star ({fill} : StarProps) {
    if(fill ===0 ){
      return <StarEmpty />
    }
    else if(fill ===1){
      return <StartFull />
    }
    else {
      return <StartHald />
    }
  }

function MovieStars({score}: Props ) {

const fills = getFills(score);

    return(
        <div className="dsmovie-stars-container">
  <Start fill={fills[0]} />
  <Start fill={fills[1]} />
  <Start fill={fills[2]} />
  <Start fill={fills[3]} />
  <Start fill={fills[4]} />

</div>
    );
}

export default MovieStars;
